(190, 'widget_illdy_person', 'a:4:{s:12:\"_multiwidget\";i:1;i:5;a:8:{s:5:\"title\";s:13:\"Mark Lawrance\";s:5:\"image\";s:101:\"https://cdn.colorlib.com/illdy/wp-content/themes/illdy/layout/images/front-page/front-page-team-1.jpg\";s:8:\"position\";s:12:\"Web Designer\";s:5:\"entry\";s:42:\"Creative, detail-oriented, always focused.\";s:12:\"facebook_url\";s:1:\"#\";s:11:\"twitter_url\";s:1:\"#\";s:12:\"linkedin_url\";s:1:\"#\";s:5:\"color\";s:7:\"#f18b6d\";}i:4;a:8:{s:5:\"title\";s:12:\"Jane Stenton\";s:5:\"image\";s:101:\"https://cdn.colorlib.com/illdy/wp-content/themes/illdy/layout/images/front-page/front-page-team-2.jpg\";s:8:\"position\";s:14:\"SEO Specialist\";s:5:\"entry\";s:59:\"Curious, tech-geeck and gets serious when it comes to work.\";s:12:\"facebook_url\";s:1:\"#\";s:11:\"twitter_url\";s:1:\"#\";s:12:\"linkedin_url\";s:1:\"#\";s:5:\"color\";s:7:\"#f1d204\";}i:2;a:8:{s:5:\"title\";s:10:\"John Smith\";s:5:\"image\";s:101:\"https://cdn.colorlib.com/illdy/wp-content/themes/illdy/layout/images/front-page/front-page-team-3.jpg\";s:8:\"position\";s:9:\"Developer\";s:5:\"entry\";s:51:\"Enthusiastic, passionate with great sense of humor.\";s:12:\"facebook_url\";s:1:\"#\";s:11:\"twitter_url\";s:1:\"#\";s:12:\"linkedin_url\";s:1:\"#\";s:5:\"color\";s:7:\"#6a4d8a\";}}', 'yes'),
